코드get_mean &lt;- function(dataframe) {
  dataframe |&gt;
    summarise(표본평균체중 = mean(body_mass_g, na.rm = TRUE )) |&gt; 
    pull(표본평균체중)
}


trials |&gt; 
  enframe() |&gt; 
  mutate(sample = map(value, get_srs)) |&gt; 
  mutate(sample_mean = map_dbl(sample, get_mean)) |&gt; 
  summarise(mean(sample_mean))

